#!/usr/bin/env bash

source ./utils.sh

e_header "Installing Rectangle.app keyboard shortcuts"

defaults write com.knollsoft.Rectangle SUEnableAutomaticChecks -bool true
defaults write com.knollsoft.Rectangle allowAnyShortcut -bool true
defaults write com.knollsoft.Rectangle launchOnLogin -bool true

defaults write com.knollsoft.Rectangle leftHalf -data 
defaults write com.knollsoft.Rectangle rightHalf -data 
defaults write com.knollsoft.Rectangle topHalf -data 
defaults write com.knollsoft.Rectangle bottomHalf -data 
defaults write com.knollsoft.Rectangle topLeft -data 
defaults write com.knollsoft.Rectangle topRight -data 
defaults write com.knollsoft.Rectangle bottomLeft -data 
defaults write com.knollsoft.Rectangle bottomRight -data 

e_success "Done!"
